static int currentSelected = 0;

private void jBShowIconActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBShowIconActionPerformed
    switch(currentSelected)
    {
        case 0:
            if(JRBSimple.isSelected())
            {
                jLRoomIcon.setIcon(new ImageIcon("./src/lab5/AlArabRoom.jpg"));
            }
            else if(JRBDouble.isSelected())
            {
                jLRoomIcon.setIcon(new ImageIcon("./src/lab5/AlArabDouble.jpg"));
            }
            else
            {
                jLRoomIcon.setIcon(new ImageIcon("./src/lab5/AlArabSuite.jpg"));
            }
            break;
        case 1:
            if(JRBSimple.isSelected())
            {
                jLRoomIcon.setIcon(new ImageIcon("./src/lab5/HiltonSingle.jpg"));
            }
            else if(JRBDouble.isSelected())
            {
                jLRoomIcon.setIcon(new ImageIcon("./src/lab5/HiltonDouble.jpg"));
            }
            else
            {
                jLRoomIcon.setIcon(new ImageIcon("./src/lab5/HiltonSuite.jpg"));
            }
            break;
        case 2:
            if(JRBSimple.isSelected())
            {
                jLRoomIcon.setIcon(new ImageIcon("./src/lab5/IceHotelSingle.jpg"));
            }
            else if(JRBDouble.isSelected())
            {
                jLRoomIcon.setIcon(new ImageIcon("./src/lab5/IceHotelDouble.jpg"));
            }
            else
            {
                jLRoomIcon.setIcon(new ImageIcon("./src/lab5/IceHotelSuite.jpg"));
            }
            break;
    }
}//GEN-LAST:event_jBShowIconActionPerformed

