public void updateOptions()
{
    String test = "Options Selected:";
    if(Breakfast.isSelected())
    {
        test += " Breakfast";
    }
    if(Dinner.isSelected())
    {
        test += " Dinner";
    }
    if(ExtraTrip.isSelected())
    {
        test += " Extra Trip";
    }
    
    jLDetails.setText(test);
}
private void BreakfastStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_BreakfastStateChanged
    updateOptions();
}//GEN-LAST:event_BreakfastStateChanged

